import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Courses from './components/Courses';
import Header from './components/Header';
import CourseDetail from './components/CourseDetail';
import CreateCourse from './components/CreateCourse';
import UserSignIn from './components/UserSignIn';
import UserSignUp from './components/UserSignUp';
import NotFound from './components/NotFound';
import axios from "axios";



class App extends Component {

   state = {
      user: ''
   }

   signIn = (email, pass) => {
      // console.log(email, pass)
     axios.get('http://localhost:5000/api/users', {
         auth: {username: email, password: pass}
     })
     .then(response => {
       if(response.status === 200) {
          localStorage.setItem('firstName', response.data.firstName);
          localStorage.setItem('lastName', response.data.lastName);
          localStorage.setItem('password', response.data.password);
          localStorage.setItem('emailAddress', response.data.emailAddress);

         const firstName = localStorage.getItem('firstName');
         const lastName = localStorage.getItem('lastName');
         const password = localStorage.getItem('password');
         const emailAddress = localStorage.getItem('emailAddress');
         this.setState({
           user: {firstName, lastName, password, emailAddress}
         });
         // this.props.history.push("/")
       }
     })
     .then(response => this.props.history.push("/"))
     .catch(error => console.log(error)
     );



   }










  render() {

     const firstName = localStorage.getItem('firstName');
     const lastName = localStorage.getItem('lastName');
     const password = localStorage.getItem('password');
     const emailAddress = localStorage.getItem('emailAddress');
     const user = {firstName, lastName, password, emailAddress}

      console.log(user)
    return (
      <BrowserRouter>
         <div className="App">
         <Route path="/" render={() => <Header user={user}/>}/>
         <Switch>
            <Route exact path="/" component={Courses}/>
            <Route path="/courses/create" component={CreateCourse}/>
            <Route path="/courses/:id" render={ props => <CourseDetail id={props.match.params.id} user={user}/>}/>
            <Route path="/signin" render={ props => <UserSignIn signIn={this.signIn}/>}/>
            <Route path="/signup" component={UserSignUp}/>
            <Route component={ NotFound }/>
         </Switch>
         </div>
      </ BrowserRouter>
    );
  }
}

export default App;
