   render() {
      const isLogin = this.props.user.emailAddress;
      console.log(isLogin)
      return(
         <div className="header">
            <div className="bounds">
               <h1 className="header--logo">Courses</h1>
               <nav>
               {isLogin ? <LogOut /> : <LogIn />}

               </nav>
            </div>
         </div>
      );
   }
}
export default Header;
