import React, { Component } from 'react';

class UserSignUp extends Component {

state = {
   firstName: '',
   lastName: '',
   emailAddress: '',
   password: '',
   confirmPassword: ''
}
onChange = event => {
   this.setState({ [event.target.name]: event.target.value})
}

onSubmit = () => {
   if (this.state.password != this.state.confirmPassword) {

   }
}
checkValid = () => {
   console.log('wtf')
}



   render() {

      return(
         <div className="bounds">
           <div className="grid-33 centered signin">
             <h1>Sign Up</h1>
             <div>
               <form>
                  <div>
                     <input id="firstName" name="firstName" onChange={this.onChange} type="text" className="" placeholder="First Name" value={this.state.firstName}/>
                     <div className="checkValid">Error validation</div>
                  </div>
                 <div><input id="lastName" name="lastName" onChange={this.onChange} type="text" className="" placeholder="Last Name" value={this.state.lastName}/></div>
                 <div><input id="emailAddress" name="emailAddress" onChange={this.onChange} type="text" className="" placeholder="Email Address" value={this.state.emailAddress}/></div>
                 <div><input id="password" name="password" onChange={this.onChange} type="password" className="" placeholder="Password" value={this.state.password}/></div>
                 <div><input id="confirmPassword" name="confirmPassword" onChange={this.onChange} type="password" className="" placeholder="Confirm Password"
                     value={this.state.confirmPassword}/></div>
                 <div className="grid-100 pad-bottom">
                    <button className="button" type="submit">Sign Up</button>
                    <button className="button button-secondary">Cancel</button>
                 </div>
               </form>
             </div>
             <p>&nbsp;</p>
             <p>Already have a user account? <a href="sign-in.html">Click here</a> to sign in!</p>
           </div>
         </div>
      );
   }
}
export default UserSignUp;
