import React, { Component } from 'react';
import {Link} from "react-router-dom"

class Header extends Component {

   signIn = () => {
     //
     axios.get('http://localhost:5000/api/users', {
         auth: {username: "pasha@gmail.com", password: "12345"}
     })
     .then(response => {
       if(response.status === 200) {
         this.setState({
           user: response.data,
           currentlySignedIn: true,
           userValidationError: false,
           passwordValidationError: false
         });
         //persist the returned user record and the user's password in the
         // global state. Doing this will allow you to create and set the appropriate
         //  Authorization header on future REST API requests that require authentication.
         // localStorage.setItem("user", JSON.stringify(response.data.user));
         // localStorage.setItem("password", JSON.stringify(response.data.password)); ??
       }
     })
     .catch(err => {
       // if invalid login info
       if (err.response.status === 401) {
         // if (there's an error with user info) {
         //   this.setState({userValidationError: true});
         // }
         // if (there's an error with password){
         //   this.setState({passwordValidationError: true});
         // }
       } else if (err.response.status === 404) {
         // this.state.history.push('/notfound')
       } else {
         //if it's a 500 error
         // this.state.history.push('/error');
       }
     });
   }





















   render() {
      return(
         <div className="header">
            <div className="bounds">
               <h1 className="header--logo">Courses</h1>
               <nav>
                  <Link className="signup" to="/signout">Sign Up</Link>
                  <Link className="signin" to="/signin">Sign In</Link>
               </nav>
            </div>
         </div>
      );
   }
}
export default Header;
