import React, { Component } from 'react';
import axios from "axios";


class CreateCourse extends Component {

state = {
   validTitle: 'Please provide a value for "Title" - min 5 characters',
   validDescrip: 'Please provide a value for "Description" - min 5 characters',
   valueTitle: "",
   valueDescrip: "",
   title: "",
   description: "",
   estimatedTime: "",
   materialsNeeded: "",
}

validTitle = (event) => {
   console.log("WTF")
    this.setState({valueTitle: event.target.value});
    console.log(event.target.value)
}
  validDescrip = (event) => {
      this.setState({valueDescrip: event.target.value});
}
removeElement () {
   const validWrap = document.querySelector(".validation-wrapper");
   if (validWrap) {
      if(this.state.valueTitle.length > 5 && this.state.valueDescrip.length > 5) {
         validWrap.style.display = "none"
      } else {
         validWrap.style.display = "block"
      }
   }
}
onChange = event => {
   this.setState({ [event.target.name]: event.target.value });
}


onSubmit = event => {
   const {
     title,
     description,
     estimatedTime,
     materialsNeeded

   } = this.state;
   const user = '57029ed4795118be119cc437';
   event.preventDefault();
   const course = {
     title,
     description,
     estimatedTime,
     materialsNeeded,
     user
   };


  // axios.post("http://localhost:5000/api/courses", course)
  //   .then(res => console.log(res))
  //   .catch(error => console.log(error)
  //   );
}





   render() {
      // console.log(this.state.title)
      // console.log(this.state.description)
      // console.log(this.state.materialsNeeded)
      // console.log(this.state.estimatedTime)
      const {
        title,
        description,
        estimatedTime,
        materialsNeeded,
        // user
      } = this.state;
this.removeElement ();
      return(
         <div className="bounds course--detail">
            <h1>Create Course</h1>
            <div>
               <div className = "validation-wrapper">
                <h2 className="validation--errors--label">Validation errors</h2>
                <div className="validation-errors">
                  <ul>
                     <li className="validTitle">{this.state.valueTitle.length > 5 ? "" : this.state.validTitle}</li>
                     <li className="validDescrip">{this.state.valueDescrip.length > 5 ? "" : this.state.validDescrip}</li>
                  </ul>
                </div>
               </div>
            </div>
            <form onSubmit={this.onSubmit}>
               <div className="grid-66">
                 <div className="course--header">
                  <h4 className="course--label">Course</h4>
                  <div>
                     <input id="title" onChange={this.onChange}  value={title} name="title" type="text" className="input-title course--title--input" placeholder="Course title..."/>
                  </div>
                   <p>By Joe Smith</p>
                 </div>
                 <div className="course--description">
                   <div><textarea id="description" onChange={this.validDescrip, this.onChange} value={description} name="description" className="" placeholder="Course description..."></textarea></div>
                 </div>
               </div>
               <div className="grid-25 grid-right">
                 <div className="course--stats">
                   <ul className="course--stats--list">
                     <li className="course--stats--list--item">
                       <h4>Estimated Time</h4>
                       <div><input id="estimatedTime" onChange={this.onChange} value={estimatedTime} name="estimatedTime" type="text" className="course--time--input"
                           placeholder="Hours"/></div>
                     </li>
                     <li className="course--stats--list--item">
                       <h4>Materials Needed</h4>
                       <div><textarea id="materialsNeeded" onChange={this.onChange} name="materialsNeeded" value={materialsNeeded} className="" placeholder="List materials..."></textarea></div>
                     </li>
                   </ul>
                 </div>
               </div>
               <div className="grid-100 pad-bottom">
                  <button className="button" type="submit">Create Course</button>
                  <button className="button button-secondary">Cancel</button>
               </div>
            </form>


         </div>
      );
   }
}
export default CreateCourse;
