import React, { Component } from 'react';

class CreateCourse extends Component {
   componentDidMount(){

      const validatioErr = document.querySelector('.validation-errors');
      const title = document.querySelector('#title');
      const description = document.querySelector('#description');
      console.log(validatioErr.firstChild.children.length)
      // validatioErr.firstChild.innerHTML += `<li className="validTitle">Please provide a value for "Title"</li>`;
      const validTitle = document.querySelector('.validTitle');
      title.addEventListener("input", el => {

         if (el.target.value.length <= 0) {
            // validTitle

            console.log(validTitle)
            validTitle.remove();
            // validTitle.style.display = 'list-item';
         }
         // console.log(el.target.value.length);
         // console.log(title.value);
         // console.log(description.value);
      })


   }
   render() {

      return(
         <div className="bounds course--detail">
            <h1>Create Course</h1>
            <div>
               <div>
                <h2 className="validation--errors--label">Validation errors</h2>
                <div className="validation-errors">
                  <ul>
                     <li className="validTitle">Please provide a value for "Title"</li>
                     <li className="validDescrip">Please provide a value for "Description"</li>
                  </ul>
                </div>
               </div>
            </div>
            <form>
               <div className="grid-66">
                 <div className="course--header">
                  <h4 className="course--label">Course</h4>
                  <div>
                     <input id="title" name="title" type="text" className="input-title course--title--input" placeholder="Course title..."/>
                  </div>
                   <p>By Joe Smith</p>
                 </div>
                 <div className="course--description">
                   <div><textarea id="description" name="description" className="" placeholder="Course description..."></textarea></div>
                 </div>
               </div>
               <div className="grid-25 grid-right">
                 <div className="course--stats">
                   <ul className="course--stats--list">
                     <li className="course--stats--list--item">
                       <h4>Estimated Time</h4>
                       <div><input id="estimatedTime" name="estimatedTime" type="text" className="course--time--input"
                           placeholder="Hours"/></div>
                     </li>
                     <li className="course--stats--list--item">
                       <h4>Materials Needed</h4>
                       <div><textarea id="materialsNeeded" name="materialsNeeded" className="" placeholder="List materials..."></textarea></div>
                     </li>
                   </ul>
                 </div>
               </div>
               <div className="grid-100 pad-bottom">
                  <button className="button" type="submit">Create Course</button>
                  <button className="button button-secondary">Cancel</button>
               </div>
            </form>


         </div>
      );
   }
}
export default CreateCourse;
