import React, { Component } from 'react';


class CreateCourse extends Component {

state = {
   validTitle: 'Please provide a value for "Title" - min 5 characters',
   validDescrip: 'Please provide a value for "Description" - min 5 characters',
   valueTitle: "",
   valueDescrip: ""
}


validTitle = (event) => {
    this.setState({valueTitle: event.target.value});
}
  validDescrip = (event) => {
      this.setState({valueDescrip: event.target.value});
}
removeElement () {
   const validWrap = document.querySelector(".validation-wrapper");
   if (validWrap) {
      if(this.state.valueTitle.length > 5 && this.state.valueDescrip.length > 5) {
         validWrap.style.display = "none"
      } else {
         validWrap.style.display = "block"
      }

   }

}
   render() {
this.removeElement ()
      return(
         <div className="bounds course--detail">
            <h1>Create Course</h1>
            <div>
               <div className = "validation-wrapper">
                <h2 className="validation--errors--label">Validation errors</h2>
                <div className="validation-errors">
                  <ul>
                     <li className="validTitle">{this.state.valueTitle.length > 5 ? "" : this.state.validTitle}</li>
                     <li className="validDescrip">{this.state.valueDescrip.length > 5 ? "" : this.state.validDescrip}</li>
                  </ul>
                </div>
               </div>
            </div>
            <form action="/" method="post">

               <div className="grid-100 pad-bottom">
                  <button className="button" type="submit">Create Course</button>
                  <button className="button button-secondary">Cancel</button>
               </div>
            </form>


         </div>
      );
   }
}
export default CreateCourse;
