import React, { Component } from 'react';
import { BrowserRouter, Route, Switch } from 'react-router-dom';
import Courses from './components/Courses';
import Header from './components/Header';
import CourseDetail from './components/CourseDetail';
import CreateCourse from './components/CreateCourse';
import UserSignIn from './components/UserSignIn';
import UserSignUp from './components/UserSignUp';
import NotFound from './components/NotFound';
import axios from "axios";



class App extends Component {

   state = {
      user: ''
   }

   signIn = (email, pass) => {
      // console.log(email, pass)
     axios.get('http://localhost:5000/api/users', {
         auth: {username: email, password: pass}
     })
     .then(response => {
       if(response.status === 200) {
         this.setState({
           user: response.data
         });
       }
     })
     .catch(err => {
       // if invalid login info
       if (err.response.status === 401) {
         // }
       } else if (err.response.status === 404) {
         this.state.history.push('/notfound')
       } else {
         //if it's a 500 error
         // this.state.history.push('/error');
       }
     });
   }










  render() {
      // console.log(this.state.user)
    return (
      <BrowserRouter>
         <div className="App">
         <Route path="/" render={() => <Header user={this.state.user}/>}/>
         <Switch>
            <Route exact path="/" component={Courses}/>
            <Route path="/courses/create" component={CreateCourse}/>
            <Route path="/courses/:id" render={ props => <CourseDetail id={props.match.params.id}/>}/>
            <Route path="/signin" render={ () => <UserSignIn signIn={this.signIn}/>}/>
            <Route path="/signup" component={UserSignUp}/>
            <Route component={ NotFound }/>
         </Switch>
         </div>
      </ BrowserRouter>
    );
  }
}

export default App;
